    private void btnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCalcularActionPerformed
        // TODO add your handling code here:
        String nome = txtNome.getText();
        double distancia = Double.valueOf(txtDistancia.getText());
        double consumo = Double.valueOf(txtComsumo.getText());
        double preco = Double.valueOf(txtprecoCombustivel.getText());
        double resultado;
        resultado = distancia * consumo * preco;
        txtMensagem.setText(String.valueOf(nome + " o seu gasto com combust√≠vel e R$" + resultado));
        
        
    }//GEN-LAST:event_btnCalcularActionPerformed

